import React, { Component } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { ActionSubmitLogin } from '../store/actions';


class Login extends Component {

  render() {
    return (
      <div>
        <form action="">
          <label for="email">Email do Gravatar:</label>
          <input htmlFor="email" type="email" data-testid="input-player-name" />
          <label for="player-name">Nome do Jogador:</label>
          <input htmlFor="player-name" type="text" data-testid="input-gravatar-email" />
          <input type="submit" value="Jogar" data-testid="btn-play" disabled={false} />
        </form>
      </div>
    );
  }
}


const mapStateToProps = (state) => ({ state });


const mapDispatchToProps = (dispatch) => bindActionCreators(
  {
    SubmitLogin: ActionSubmitLogin,
  }, dispatch,
);
Login.propTypes = {
};

Login.defaultProps = {
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
