import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import PropTypes from 'prop-types';
import 'bulma/css/bulma.css';

class Ranking extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }

  restartGame(history) {
    console.log(this.props);
    localStorage.removeItem('state');
    history.push('/');
  }

  renderRankingList() {
    console.log(this.props);
    const ranking = JSON.parse(localStorage.getItem('ranking'));
    return (
      <ol>
        {ranking.map(({ name, gravatarEmail, score }, index) => (
          <li className="card-content-item">
            <div>
              <img className="image is-20x20" src={gravatarEmail} alt="profile-pic" />
              <span data-testid={`player-name-${index}`}>{`${name} - `}</span>
              <span data-testid={`player-score-${index}`}>{`${score} pontos`}</span>
            </div>
          </li>
        ))}
      </ol>
    );
  }

  renderGoHomeButton() {
    const { history } = this.props;
    return (

      <button
        type="button"
        className="button is-info card-footer-item is-centered"
        data-testid="btn-go-home"
        onClick={() => this.restartGame(history)}
      >
        JOGAR NOVAMENTE
      </button>

    );
  }

  render() {
    return (
      <div
        style={{
          display: 'flex',
          justifyContent: 'center',
        }}
      >
        <div
          className="card"
          style={{
            width: '400px',
          }}
        >
          <div className="card-header-title is-centered" data-testid="ranking-title">
            Ranking
          </div>
          <div className="card-content">
            {this.renderRankingList()}
          </div>
          <div className="card-footer">
            {this.renderGoHomeButton()}
          </div>
        </div>
      </div>
    );
  }
}


const mapStateToProps = ({
  ReducerPlayer: {
    name,
    gravatarEmail,
    score,
  },
}) => ({
  name,
  gravatarEmail,
  score,
});

const mapDispatchToProps = (dispatch) => bindActionCreators(
  {
  }, dispatch,
);

Ranking.propTypes = {
  name: PropTypes.string.isRequired,
  gravatarEmail: PropTypes.string.isRequired,
  score: PropTypes.number.isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(Ranking);
