import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import PropTypes from 'prop-types';
import 'bulma/css/bulma.css';

class Ranking extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }

  getRanking() {
    const {
      name,
      gravatarEmail,
      score,
    } = this.props;
    const newPlayer = { name, gravatarEmail, score };
    const previousRanking = localStorage.getItem('ranking') !== null ? JSON.parse(localStorage.getItem('ranking')) : null;
    if (previousRanking === null) {
      return [
        newPlayer,
      ];
    }
    return [
      ...previousRanking,
      newPlayer,
    ].sort((a, b) => b.score - a.score);
  }

  updateRanking() {
    const newRanking = this.getRanking();
    localStorage.setItem('ranking', JSON.stringify(newRanking));
  }

  async renderRankingList() {
    await this.updateRanking();
    const ranking = JSON.parse(localStorage.getItem('ranking'));
    console.log(ranking);
  }

  renderGoHomeButton() {
    console.log(this.props);
    return (
      <Link to="/" data-testid="btn-go-home">
        JOGAR NOVAMENTE
      </Link>
    );
  }

  render() {
    return (
      <div
        style={{
          display: 'flex',
          justifyContent: 'center',
        }}
      >
        <div
          className="card"
          style={{
            width: '400px',
          }}
        >
          <div className="card-header">
            Ranking
          </div>
          <div className="card-content">
            {this.renderRankingList()}
          </div>
          <div className="card-content">
            {this.renderGoHomeButton()}
          </div>
        </div>
      </div>
    );
  }
}


const mapStateToProps = ({
  ReducerPlayer: {
    name,
    gravatarEmail,
    score,
  },
}) => ({
  name,
  gravatarEmail,
  score,
});

const mapDispatchToProps = (dispatch) => bindActionCreators(
  {
  }, dispatch,
);

Ranking.propTypes = {
  name: PropTypes.string.isRequired,
  gravatarEmail: PropTypes.string.isRequired,
  score: PropTypes.number.isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(Ranking);
